import React from 'react';
import { useNavigate } from 'react-router-dom';
import './EventCard.css';

export default function EventCard({ event }) {
  const navigate = useNavigate();

  const handleVolunteerClick = () => {
    navigate('/volunteer', { state: { event } });
  };

  return (
    <div className="event-card">
      {/* Image container - now absolutely above text */}
      <div className="event-image-container">
        <img 
          src={event.image} 
          alt={event.title}
          className="event-image"
        />
      </div>

      {/* Text container - strictly below image */}
      <div className="text-container">
        <h3>{event.title}</h3>
        <div className="event-meta">
          <span><i className="fas fa-calendar-alt"></i> {event.date}</span>
          <span><i className="fas fa-map-marker-alt"></i> {event.location}</span>
        </div>
        <p className="event-description">{event.description}</p>

        {event.volunteerPositions?.length > 0 && (
          <button className="volunteer-button" onClick={handleVolunteerClick}>
            <i className="fas fa-hands-helping"></i> Volunteer
          </button>
        )}
      </div>
    </div>
  );
}
