import React, { useState } from 'react';

const UserDashboard = () => {
  const [event, setEvent] = useState({
    title: '',
    image: '',
    date: '',
    location: '',
    description: '',
    volunteerJobs: [] // New field for volunteer positions
  });
  const [newJob, setNewJob] = useState('');

  const handleAddJob = () => {
    if (newJob.trim()) {
      setEvent({
        ...event,
        volunteerJobs: [...event.volunteerJobs, newJob.trim()]
      });
      setNewJob('');
    }
  };

  const handleRemoveJob = (index) => {
    const updatedJobs = [...event.volunteerJobs];
    updatedJobs.splice(index, 1);
    setEvent({...event, volunteerJobs: updatedJobs});
  };

  return (
    <div className="user-dashboard">
      <h2>Create New Event</h2>
      {/* Existing event fields... */}
      
      <div className="volunteer-section">
        <h3>Volunteer Positions</h3>
        <div className="job-input">
          <input
            type="text"
            value={newJob}
            onChange={(e) => setNewJob(e.target.value)}
            placeholder="Add volunteer position (e.g., 'Registration Desk')"
          />
          <button type="button" onClick={handleAddJob}>Add</button>
        </div>
        
        <div className="job-list">
          {event.volunteerJobs.map((job, index) => (
            <div key={index} className="job-item">
              <span>{job}</span>
              <button 
                type="button" 
                onClick={() => handleRemoveJob(index)}
                className="remove-btn"
              >
                Ã—
              </button>
            </div>
          ))}
        </div>
      </div>
      
      <button type="submit">Submit Event</button>
    </div>
  );
};
