import React from 'react';
import { useNavigate } from 'react-router-dom';
import './EventCard.css';

export default function EventCard({ event }) {
  const navigate = useNavigate();

  const handleVolunteerClick = () => {
    navigate('/volunteer', { state: { event } });
  };

  // Format date to be more readable
  const formatDate = (dateString) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('en-US', {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return dateString; // Fallback to original string
    }
  };

  return (
    <div className="event-card">
      {/* Image - Strictly on top */}
      <div className="event-image-container">
        <img 
          src={event.image} 
          alt={event.title}
          className="event-image"
          onError={(e) => {
            e.target.src = "https://images.unsplash.com/photo-1600566752225-555bdd87c640?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80";
          }}
        />
      </div>

      {/* Content - Strictly below image */}
      <div className="event-content">
        <h3 className="event-title">{event.title}</h3>
        
        <div className="event-details">
          <div className="event-info-row">
            <i className="fas fa-calendar-alt"></i>
            <span className="event-date">{formatDate(event.date)}</span>
          </div>
          
          <div className="event-info-row">
            <i className="fas fa-map-marker-alt"></i>
            <span className="event-location">{event.location}</span>
          </div>
        </div>

        {event.description && (
          <p className="event-description">{event.description}</p>
        )}

        {event.volunteerPositions?.length > 0 && (
          <div className="volunteer-section">
            <h4 className="volunteer-title">Volunteer Opportunities:</h4>
            <div className="volunteer-positions">
              {event.volunteerPositions.map((position, index) => (
                <span key={index} className="position-tag">
                  {position}
                </span>
              ))}
            </div>
            <button className="volunteer-btn" onClick={handleVolunteerClick}>
              <i className="fas fa-hand-heart"></i>
              Apply to Volunteer
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
