import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../utils/api';
import './VolunteerPage.css';

export default function VolunteerPage() {
  const [volunteers, setVolunteers] = useState([]);
  const [formData, setFormData] = useState({
    skills: [],
    age: '',
    gender: '',
    contactEmail: ''
  });
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check login status
    setIsLoggedIn(!!localStorage.getItem('user'));

    // Load public volunteers
    api.get('/volunteers/public')
      .then(res => setVolunteers(res.data))
      .catch(console.error);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/volunteers/register', {
        ...formData,
        skills: formData.skills.join(',')
      });
      alert('Registration successful!');
      window.location.reload();
    } catch (err) {
      alert('Registration failed');
    }
  };

  return (
    <div className="volunteer-page">
      <h1>Community Volunteers</h1>

      {/* Volunteers List - Visible to all */}
      <div className="volunteer-list">
        {volunteers.map(v => (
          <div key={v.id} className="volunteer-card">
            <h3>Skills: {v.skills.join(', ')}</h3>
            <p>Age: {v.age}</p>
            <p>Gender: {v.gender}</p>
          </div>
        ))}
      </div>

      {/* Registration Form - Only for logged-in users */}
      {isLoggedIn ? (
        <form onSubmit={handleSubmit}>
          {/* Keep your existing form fields exactly as they are */}
        </form>
      ) : (
        <div className="login-prompt">
          <p>Want to volunteer? <Link to="/login">Login to register</Link></p>
        </div>
      )}
    </div>
  );
}
