import React, { useState } from 'react';

const EventCard = ({ event }) => {
  const [selectedJob, setSelectedJob] = useState('');
  const [isVolunteering, setIsVolunteering] = useState(false);

  const handleVolunteer = () => {
    if (selectedJob) {
      alert(`Thank you for volunteering as ${selectedJob} for ${event.title}!`);
      setIsVolunteering(true);
    }
  };

  return (
    <div className="event-card">
      {/* Existing event card content... */}

      {event.volunteerJobs?.length > 0 && (
        <div className="volunteer-options">
          <h4>Volunteer Opportunities:</h4>
          {!isVolunteering ? (
            <>
              <select 
                value={selectedJob} 
                onChange={(e) => setSelectedJob(e.target.value)}
              >
                <option value="">Select a position</option>
                {event.volunteerJobs.map((job, index) => (
                  <option key={index} value={job}>{job}</option>
                ))}
              </select>
              <button 
                onClick={handleVolunteer}
                disabled={!selectedJob}
              >
                Volunteer
              </button>
            </>
          ) : (
            <p className="volunteer-confirmation">
              âœ“ You're volunteering as {selectedJob}
            </p>
          )}
        </div>
      )}
    </div>
  );
};
