import React from 'react';
import './EventCard.css';

export default function EventCard({ event }) {
  return (
    <div className="event-card">
      <div className="event-image" style={{ backgroundImage: `url(${event.image})` }} />
      <div className="event-details">
        <h3>{event.title}</h3>
        <div className="event-meta">
          <span>{event.date}</span>
          <span>{event.location}</span>
        </div>
        <p className="event-description">{event.description}</p>

        {/* NEW: Volunteer positions display */}
        {event.volunteerPositions?.length > 0 && (
          <div className="volunteer-section">
            <h4>Volunteer Roles Needed:</h4>
            <div className="volunteer-positions">
              {event.volunteerPositions.map((pos, index) => (
                <span key={index} className="volunteer-tag">{pos}</span>
              ))}
            </div>
            <button className="volunteer-button">Sign Up to Volunteer</button>
          </div>
        )}
      </div>
    </div>
  );
}
