import { Link } from 'react-router-dom';

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    setIsLoggedIn(!!localStorage.getItem('user'));
  }, []);

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo">
          CommunityBoard
        </Link>

        <ul className="nav-menu">
          {/* Always show Volunteer link */}
          <li className="nav-item">
            <Link to="/volunteer" className="nav-links">
              Volunteers
            </Link>
          </li>

          {/* Conditional logged-in items */}
          {isLoggedIn && (
            <>
              <li className="nav-item">
                <Link to="/user" className="nav-links">
                  Dashboard
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/my-events" className="nav-links">
                  My Events
                </Link>
              </li>
            </>
          )}

          {/* Login/Register or User Profile */}
          {!isLoggedIn ? (
            <>
              <li className="nav-item">
                <Link to="/login" className="nav-links">
                  Login
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/register" className="nav-links">
                  Register
                </Link>
              </li>
            </>
          ) : (
            <li className="nav-item">
              <button onClick={handleLogout} className="nav-links logout-btn">
                Logout
              </button>
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
};
