import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../utils/api';
import './VolunteerPage.css';

export default function VolunteerPage() {
  const [volunteers, setVolunteers] = useState([]);
  const [formData, setFormData] = useState({
    skills: [],
    age: '',
    gender: '',
    contactEmail: ''
  });
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const user = localStorage.getItem('user');
    setIsLoggedIn(!!user);

    // Load public volunteers
    api.get('/volunteers/public')
      .then(response => setVolunteers(response.data))
      .catch(console.error);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/volunteers/register', {
        ...formData,
        skills: formData.skills.join(',')
      });
      alert('Registration successful!');
      window.location.reload();
    } catch (err) {
      alert('Registration failed. Please try again.');
    }
  };

  return (
    <div className="volunteer-page">
      <h1>Community Volunteers</h1>

      {/* Public Volunteer List */}
      <div className="volunteer-list">
        {volunteers.map(v => (
          <div key={v.id} className="volunteer-card">
            <h3>Skills: {v.skills.join(', ')}</h3>
            <p>Age: {v.age}</p>
            <p>Gender: {v.gender}</p>
          </div>
        ))}
      </div>

      {/* Registration Form (logged-in only) */}
      {isLoggedIn ? (
        <form onSubmit={handleSubmit}>
          <input
            value={formData.skills}
            onChange={(e) => setFormData({...formData, skills: e.target.value.split(',')})}
            placeholder="Skills (comma separated)"
            required
          />
          <input
            type="number"
            value={formData.age}
            onChange={(e) => setFormData({...formData, age: e.target.value})}
            placeholder="Age"
            required
          />
          <select
            value={formData.gender}
            onChange={(e) => setFormData({...formData, gender: e.target.value})}
            required
          >
            <option value="">Select Gender</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="other">Other</option>
          </select>
          <button type="submit">Register</button>
        </form>
      ) : (
        <div className="login-prompt">
          <Link to="/login">Login to register as volunteer</Link>
        </div>
      )}
    </div>
  );
}
